CREATE TABLE ADDRESS (
	ADDRESS_ID int auto_increment primary key NOT NULL,
	SETTLEMENT_ID int NULL,
	ADDRESS varchar(75) NULL
);

CREATE TABLE AREA (
	AREA_ID int auto_increment primary key NOT NULL,
	REGION_ID int NOT NULL,
	NAME varchar(50) NOT NULL
);

CREATE TABLE BASE_DISCIPLINE (
	BASE_DISCIPLINE_ID int auto_increment primary key NOT NULL,
	SUBFACULTY_ID int NOT NULL,
	NAME varchar(75) NOT NULL
);

CREATE TABLE BENEFITS (
	BENEFIT_ID int auto_increment primary key NOT NULL,
	NAME varchar(50) NULL
);

CREATE TABLE CARD (
	CARD_ID int auto_increment primary key NOT NULL,
	COURSE smallint NOT NULL,
	SEMESTER bit NOT NULL,
	YYEAR smallint NOT NULL
);

CREATE TABLE COUNTRY (
	COUNTRY_ID int auto_increment primary key NOT NULL,
	NAME varchar(30) NOT NULL
);

CREATE TABLE DEPARTAMENT (
	DEPARTMANENT_ID int auto_increment primary key NOT NULL,
	NAME varchar(50) NOT NULL
);

CREATE TABLE DISCIPLINE (
	DISCIPLINE_ID int auto_increment primary key NOT NULL,
	EDUCATION_PLAN_ID int NULL,
	BASE_DISCIPLINE_ID int NULL,
	HOURS smallint NOT NULL,
	Independent_Hours smallint NULL,
	Audience_Hours smallint NULL,
	SEMESTER bit NOT NULL,
	COURSE smallint NOT NULL,
	YYEAR smallint NOT NULL,
	CHECKING_FORM smallint NOT NULL
);

CREATE TABLE DISCIPLINE_TEACHER (
	DISCIPLINE_TEACHER_ID int auto_increment primary key NOT NULL,
	TEACHER_ID int NOT NULL,
	DISCIPLINE_ID int NOT NULL
);

CREATE TABLE EDUCATION_PLAN (
	EDUCATION_PLAN_ID int auto_increment primary key NOT NULL,
	SPECIALITY_ID int NOT NULL,
	DATA_START TIMESTAMP NULL,
	DATA_END TIMESTAMP NULL
);

CREATE TABLE EXAM_LIST (
	EXAM_LIST_ID int auto_increment primary key NOT NULL,
	NUMBER varchar(15) NULL,
	TYPE smallint NULL,
	DATE_CREATION TIMESTAMP NOT NULL
);

CREATE TABLE EXAM_MARK (
	EXAM_MARK_ID int auto_increment primary key NOT NULL,
	EXAM_LIST_ID int NOT NULL,
	STUDENT_GROUP_ID int NOT NULL,
	DISCIPLINE_ID int NOT NULL,
	STUDENT_ID int NOT NULL,
	VVALUE smallint NULL
);

CREATE TABLE FACULTY (
	FACULTY_ID int auto_increment primary key NOT NULL,
	NAME varchar(50) NOT NULL,
	SHORT_NAME varchar(7) NOT NULL,
	FACULTY_CODE smallint NULL
);

CREATE TABLE FACULTY_GROUP_CODE (
	FACULTY_GROUP_CODE_ID int auto_increment primary key NOT NULL,
	FACULTY_ID int NULL,
	COMMENT varchar(50) NULL
);

CREATE TABLE HOSPICE (
	HOSPICE_ID int auto_increment primary key NOT NULL,
	NAME varchar(75) NULL,
	STREET varchar(50) NULL,
	HOUSE smallint NULL,
	BUILDING smallint NULL
);

CREATE TABLE LANGUAGES (
	LANGUAGE_ID int auto_increment primary key NOT NULL,
	NAME varchar(25) NULL
);

CREATE TABLE MARK (
	MARK_ID int auto_increment primary key NOT NULL,
	DISCIPLINE_ID int NOT NULL,
	EXAM_LIST_ID int NULL,
	STUDENT_ID int NOT NULL,
	STUDENT_GROUP_ID int NOT NULL,
	VVALUE smallint NULL
);

CREATE TABLE NOTE (
	NOTE_ID int auto_increment primary key NOT NULL,
	STUDENT_ID int NOT NULL,
	CARD_ID int NOT NULL,
	STUDENT_GROUP_ID int NOT NULL,
	FREE_ATTENDANCE BOOLEAN NULL,
	REBUKE varchar(12) NULL,
	LAST_EXAM_DATE TIMESTAMP NULL,
	SESSION_PROLONGATION_DATE TIMESTAMP NULL,
	SESSION_CLOSING_DATE TIMESTAMP NULL,
	STUDENTGRANT varchar(7) NULL,
	PRACTICE varchar(7) NULL
);

CREATE TABLE OFFICE_ORDER (
	OFFICE_ORDER_ID int auto_increment primary key NOT NULL,
	ORDER_CATEGORY_ID int NOT NULL,
	PATH_TO_FILE varchar(100) NULL,
	EXTERNAL_NUMBER varchar(25) NULL,
	REASON varchar(512) NULL,
	SUBJECT varchar(2048) NULL,
	DATE_ORDER TIMESTAMP NULL,
	ISSIGNED BOOLEAN NULL,
	SIGNER_POSITION varchar(50) NULL,
	SIGNER_NAME varchar(100) NULL,
	MAKER_POSITION varchar(50) NULL,
	MAKER_NAME varchar(100) NULL,
	COORDINATOR1_POSITION varchar(50) NULL,
	COORDINATOR1_NAME varchar(100) NULL,
	COORDINATOR2_POSITION varchar(50) NULL,
	COORDINATOR2_NAME varchar(100) NULL,
	COORDINATOR3_POSITION varchar(50) NULL,
	COORDINATOR3_NAME varchar(100) NULL,
	COORDINATOR4_POSITION varchar(50) NULL,
	COORDINATOR4_NAME varchar(100) NULL,
	COORDINATOR5_POSITION varchar(50) NULL,
	COORDINATOR5_NAME varchar(100) NULL,
	COORDINATOR6_POSITION varchar(50) NULL,
	COORDINATOR6_NAME varchar(100) NULL
);

CREATE TABLE ORDER_CATEGORY (
	ORDER_CATEGORY_ID int auto_increment primary key NOT NULL,
	ORDER_CATEGORY varchar(100) NULL
);

CREATE TABLE PERSONAL (
	PERSONAL_ID int auto_increment primary key NOT NULL,
	POSITION_ID int NOT NULL,
	DEPARTMANENT_ID int NOT NULL,
	NAME varchar(25) NULL,
	PATRONYMIC varchar(25) NULL,
	FAMILY_NAME varchar(50) NULL
);

CREATE TABLE POSITION (
	POSITION_ID int auto_increment primary key NOT NULL,
	NAME varchar(50) NULL
);

CREATE TABLE REGION (
	REGION_ID int auto_increment primary key NOT NULL,
	COUNTRY_ID int NOT NULL,
	NAME varchar(50) NOT NULL
);

CREATE TABLE SETTLEMENT (
	SETTLEMENT_ID int auto_increment primary key NOT NULL,
	SETTLEMENT_DEFINITION_ID int NULL,
	AREA_ID int NULL,
	NAME varchar(50) NOT NULL
);

CREATE TABLE SETTLEMENT_DEFINITION (
	SETTLEMENT_DEFINITION_ID int auto_increment primary key NOT NULL,
	NAME varchar(25) NOT NULL
);

CREATE TABLE SPECIALITY (
	SPECIALITY_ID int auto_increment primary key NOT NULL,
	FACULTY_ID int NULL,
	NUMBER varchar(10) NOT NULL,
	NAME varchar(75) NOT NULL,
	SHORTNAME varchar(7) NOT NULL
);

CREATE TABLE STUDENT (
	STUDENT_ID int auto_increment primary key NOT NULL,
	STUDENT_GROUP_ID int NULL,
	ADDRESS_ID int NOT NULL,
	HOSPICE_ID int NULL,
	STUDENT_STATUS_ID int NOT NULL,
	BENEFIT_ID int NOT NULL,
	COUNTRY_ID int NOT NULL,
	LANGUAGE_ID int NOT NULL,
	NAME varchar(20) NOT NULL,
	PATRONYMIC varchar(25) NOT NULL,
	FAMILY_NAME varchar(50) NOT NULL,
	STUDENT_NUMBER varchar(6) NULL,
	REGISTRATION varchar(150) NULL,
	PASSPORT_DATA varchar(150) NOT NULL,
	Passport_Number varchar(15) NULL,
	Passport_Series varchar(15) NULL,
	Passport_Delivery varchar(150) NULL,
	HOSPICE_NEEDED BOOLEAN NOT NULL,
	SEX bit NOT NULL,
	CONTRACT BOOLEAN NOT NULL,
	COURSE_LEADER BOOLEAN NULL,
	Prof_Leader BOOLEAN NULL,
	LAST_EDUCATION_PLACE varchar(20) NULL,
	YEAR_STARTING TIMESTAMP NOT NULL,
	START_SPECIALITY_ID int NULL
);

CREATE TABLE STUDENT_EVENT (
	EVENT_ID int auto_increment primary key NOT NULL,
	NAME varchar(50) NULL,
	COMMENT varchar(100) NULL
);

CREATE TABLE STUDENT_GROUP (
	STUDENT_GROUP_ID int auto_increment primary key NOT NULL,
	PERSONAL_ID int NULL,
	SPECIALITY_ID int NOT NULL,
	COURSE tinyint NOT NULL,
	EDUCATION_DURATION BOOLEAN NOT NULL,
	NUMBER varchar(7) NULL
);

CREATE TABLE STUDENT_HISTORY (
	STUDENT_HISTORY_ID int auto_increment primary key NOT NULL,
	EVENT_ID int NOT NULL,
	USER_ID int NULL,
	OFFICE_ORDER_ID int NULL,
	STUDENT_ID int NULL,
	COMMENT varchar(250) NULL,
	DATE_EVENT TIMESTAMP NULL
);

CREATE TABLE STUDENT_STATUS (
	STUDENT_STATUS_ID int auto_increment primary key NOT NULL,
	NAME varchar(50) NOT NULL
);

CREATE TABLE SUBFACULTY (
	SUBFACULTY_ID int auto_increment primary key NOT NULL,
	FACULTY_ID int NULL,
	NAME varchar(75) NOT NULL,
	SUBFACULTY_NUMBER varchar(10) NOT NULL
);

CREATE TABLE TEACHER (
	TEACHER_ID int auto_increment primary key NOT NULL,
	PERSONAL_ID int NOT NULL,
	SUBFACULTY_ID int NOT NULL
);

CREATE TABLE USER_ROLE (
	USER_ROLE_ID int auto_increment primary key NOT NULL,
	NAME varchar(25) NULL
);

CREATE TABLE USERS (
	USER_ID int auto_increment primary key NOT NULL,
	PERSONAL_ID int NULL,
	USER_ROLE_ID int NOT NULL,
	LOGIN varchar(15) NULL,
	PPASSWORD varchar(15) NULL
);


